CREATE TABLE client (id BIGSERIAL, sf_guard_user_id INT NOT NULL, firstname VARCHAR(50) NOT NULL, lastname VARCHAR(50) NOT NULL, origin VARCHAR(255), phone VARCHAR(50), email_address VARCHAR(50) NOT NULL, email_confirmed BOOLEAN DEFAULT 'false' NOT NULL, created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE destination (id BIGSERIAL, title VARCHAR(255) NOT NULL, description text, seq_num INT NOT NULL, active BOOLEAN NOT NULL, created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, slug VARCHAR(255), PRIMARY KEY(id));
CREATE TABLE message (id BIGSERIAL, client_id INT, email_address VARCHAR(50) NOT NULL, message text NOT NULL, created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE paypal_transaction (id BIGSERIAL, parent_id INT, txn_id VARCHAR(32) NOT NULL, txn_type VARCHAR(20) NOT NULL, status VARCHAR(20) NOT NULL, ipn_data text NOT NULL, created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE reservation (id BIGSERIAL, client_id INT NOT NULL, destination_id INT NOT NULL, uniqid VARCHAR(32) NOT NULL, round_trip VARCHAR(2) NOT NULL, no_pax SMALLINT NOT NULL, hotel VARCHAR(255) NOT NULL, arrival_date TIMESTAMP NOT NULL, arrival_flight_no VARCHAR(20) NOT NULL, departure_date TIMESTAMP, departure_flight_no VARCHAR(20), price numeric(10) NOT NULL, comment text, deleted BOOLEAN DEFAULT 'false' NOT NULL, status VARCHAR(20) DEFAULT '' NOT NULL, payment_date TIMESTAMP, created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE transaction (id BIGSERIAL, tx_id VARCHAR(32) NOT NULL, tx_type VARCHAR(20) NOT NULL, reservation_id INT, tx_parameters text NOT NULL, created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE transfer (id BIGSERIAL, destination_id INT NOT NULL, description text, min_pax INT NOT NULL, max_pax INT NOT NULL, round_trip VARCHAR(2) NOT NULL, price numeric(10) NOT NULL, active BOOLEAN NOT NULL, created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE sf_guard_forgot_password (id BIGSERIAL, user_id BIGINT NOT NULL, unique_key VARCHAR(255), expires_at TIMESTAMP NOT NULL, created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE sf_guard_group (id BIGSERIAL, name VARCHAR(255) UNIQUE, description VARCHAR(1000), created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE sf_guard_group_permission (group_id BIGINT, permission_id BIGINT, created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(group_id, permission_id));
CREATE TABLE sf_guard_permission (id BIGSERIAL, name VARCHAR(255) UNIQUE, description VARCHAR(1000), created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE sf_guard_remember_key (id BIGSERIAL, user_id BIGINT, remember_key VARCHAR(32), ip_address VARCHAR(50), created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE sf_guard_user (id BIGSERIAL, first_name VARCHAR(255), last_name VARCHAR(255), email_address VARCHAR(255) NOT NULL UNIQUE, username VARCHAR(128) NOT NULL UNIQUE, algorithm VARCHAR(128) DEFAULT 'sha1' NOT NULL, salt VARCHAR(128), password VARCHAR(128), is_active BOOLEAN DEFAULT 'true', is_super_admin BOOLEAN DEFAULT 'false', last_login TIMESTAMP, created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE sf_guard_user_group (user_id BIGINT, group_id BIGINT, created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(user_id, group_id));
CREATE TABLE sf_guard_user_permission (user_id BIGINT, permission_id BIGINT, created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(user_id, permission_id));
CREATE UNIQUE INDEX destination_sluggable ON destination (slug);
CREATE INDEX is_active_idx ON sf_guard_user (is_active);
